import './App.css';
import {Component} from "react";

class App extends Component{
    constructor(props) {
        super(props);
        this.state={
            user_data:{
                username:'',
                password:'',
            }

        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    componentDidMount() {
        fetch('http://127.0.0.1:8000/api/token/', {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                },

            body: JSON.stringify({
                "username":"admin1",
                "password":"admin1"
        })})
    .then(response => response.json())
            .then(cars => {
                console.log(cars)

            })
    }
        handleChange(event) {
        this.setState({user_data: {username:event.target.value,
            password:event.target.value}});
    }
    handleSubmit(event) {
        console.log('Сочинение отправлено:');
        console.log(this.state.user_data)
    }
    render() {
        return (
            <form action="">
                <label htmlFor="">
                    Username: <input type="text" name={'username'} onChange={this.handleChange}/>
                </label>
                <br/>
                <label htmlFor="">
                    Password: <input type="text" name={'password'} onChange={this.handleChange}/>
                </label>
                <br/>
                <input type="button" onClick={this.handleSubmit} value={'ok'}/>
            </form>
        )
    }
}
export default App;
